import React from 'react';
import './Visualization.css';

const Visualization = ({ result }) => {
  // If the result contains an image (static visualization)
  if (result.image) {
    return (
      <div className="visualization-container">
        <img 
          src={`data:${result.format};base64,${result.image}`} 
          alt="Visualization" 
          className="visualization-image" 
        />

        {result.output && (
          <div className="output-log">
            <h4>Output Log:</h4>
            <pre>{result.output}</pre>
          </div>
        )}
      </div>
    );
  }

  // If the result contains HTML (interactive visualization)
  if (result.html) {
    return (
      <div className="visualization-container">
        <div className="iframe-container">
          <iframe
            title="Interactive Visualization"
            srcDoc={result.html}
            className="visualization-iframe"
            sandbox="allow-scripts"
          />
        </div>

        {result.output && (
          <div className="output-log">
            <h4>Output Log:</h4>
            <pre>{result.output}</pre>
          </div>
        )}
      </div>
    );
  }

  // If no visualization data is available
  return (
    <div className="visualization-container">
      <div className="error">
        <h3>Unexpected Result Format</h3>
        <pre>The visualization couldn't be displayed. Check the server response.</pre>
        <pre>{JSON.stringify(result, null, 2)}</pre>
      </div>
    </div>
  );
};

export default Visualization;
